/* INTENTS */

const DescripcionIntent = {
    canHandle(handlerInput) {
        return Alexa.getRequestType(handlerInput.requestEnvelope) === 'IntentRequest'
            && Alexa.getIntentName(handlerInput.requestEnvelope) === 'DescripcionIntent';
    },
    handle(handlerInput) {
        const speakOutput = 'Guadalajara, conocida como la Perla de Occidente, es la'+ 
        'capital del estado de Jalisco y una de las ciudades más importantes de México.'+
        'Es famosa por su cultura mariachi, tequila y sus festividades tradicionales.'+
        'La ciudad está considerada como uno de los centros políticos, económicos, sociales,'+
        'culturales y turísticos más importantes de México.Su diversidad cultural es '+
        'tan sorprendente que ha sido visitada por muchas personas para poder vivir una'+
        'experiencia sin iguala l disfrutar de todo lo magnifico que posee.';
        
        if(Alexa.getSupportedInterfaces(handlerInput.requestEnvelope)['Alexa.Presentation.APL']){
            const aplDirective_Descripcion = createDirectivePayload(DOCUMENT_ID_DESCRIPCION, datasource_descripcion);
            handlerInput.responseBuilder.addDirective(aplDirective_Descripcion);
        }
        
        return handlerInput.responseBuilder
            .speak(speakOutput)
            //.reprompt('add a reprompt if you want to keep the session open for the user to respond')
            .getResponse();
    }
};


